function Append(strategy, req) {
  this.strategy = strategy;
  this.req = req;

  switch (strategy) {
    case "Single":
      break;
    case "Array":
      this.req.files = [];
      break;
  }
}

Append.prototype.addField = function(info) {
  switch (this.strategy) {
    case "Single":
      this.req.file = info;
      break;
    case "Array":
      this.req.files.push(info);
      break;
  }
};

module.exports = Append;
